// MarkAttendance.js
import React, { useEffect, useState } from 'react';
import { Html5Qrcode } from 'html5-qrcode';

const MarkAttendance = () => {
  const [message, setMessage] = useState('');
  const [scanned, setScanned] = useState(false);

  useEffect(() => {
    const scanQRCode = async () => {
      const devices = await Html5Qrcode.getCameras();
      if (devices && devices.length) {
        const frontCam = devices.find(device =>
          device.label.toLowerCase().includes('front')
        ) || devices[0]; // fallback to default

        const html5QrCode = new Html5Qrcode("qr-reader");

        html5QrCode.start(
          frontCam.id,
          {
            fps: 10,
            qrbox: { width: 250, height: 250 },
          },
          async (decodedText) => {
            if (!scanned) {
              setScanned(true);
              console.log("Scanned QR Token:", decodedText);

              try {
                // Try to parse the decoded text as JSON
                const parsed = JSON.parse(decodedText);
                const qrToken = parsed.qrToken;

                if (!qrToken) {
                  setMessage("Invalid QR Code content.");
                  return;
                }

                const res = await fetch('http://localhost:5000/api/attendance/mark', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({ qrToken }),
                });

                const result = await res.json();
                setMessage(result.message || 'Attendance marked');
              } catch (error) {
                console.error('QR Scan Error or API Error:', error);
                setMessage('❌ Invalid QR code or server error.');
              }

              setTimeout(() => setScanned(false), 3000);
            }
          },
          (error) => {
            console.warn('QR scan error:', error);
          }
        );
      } else {
        setMessage("No camera found.");
      }
    };

    scanQRCode();

    // Cleanup on component unmount
    return () => {
      Html5Qrcode.getCameras().then(devices => {
        if (devices.length) {
          const html5QrCode = new Html5Qrcode("qr-reader");
          html5QrCode.stop().catch(err => console.log("Stop failed", err));
        }
      });
    };
  }, [scanned]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Mark Attendance</h2>
      <p>Align the QR code in the box to scan.</p>
      <div id="qr-reader" style={{ width: '300px' }}></div>
      {message && <p><strong>{message}</strong></p>}
    </div>
  );
};

export default MarkAttendance;
