import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QRCode from 'qrcode';

function Register() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    role: 'student' // default role
  });

  const [qrDataUrl, setQrDataUrl] = useState('');
  const [registered, setRegistered] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Register user
      await axios.post('http://localhost:5000/api/auth/register', form);

      // Generate QR Code with user email or ID
      const qrValue = JSON.stringify({ email: form.email });
      const qrImageUrl = await QRCode.toDataURL(qrValue);
      setQrDataUrl(qrImageUrl);

      setRegistered(true);
    } catch (err) {
      alert('Registration failed: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleContinue = () => {
    navigate('/login');
  };

  return (
    <div className="form-container">
      {!registered ? (
        <form onSubmit={handleSubmit}>
          <h2>Register</h2>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={form.name}
            required
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            required
            onChange={handleChange}
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            required
            onChange={handleChange}
          />
          <select name="role" value={form.role} onChange={handleChange}>
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>

          <button type="submit">Register</button>
        </form>
      ) : (
        <div>
          <h2>Registration Successful ðŸŽ‰</h2>
          <p>Your QR Code (screenshot or download):</p>
          {qrDataUrl && (
            <a href={qrDataUrl} download="qrcode.png">
              <img src={qrDataUrl} alt="User QR Code" />
            </a>
          )}
          <br />
          <button onClick={handleContinue}>Continue to Login</button>
        </div>
      )}
    </div>
  );
}

export default Register;
