// src/App.js
import React from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';
import Register from './components/Register';
import Login from './components/Login';
import StudentDashboard from './components/StudentPanel';
import AdminDashboard from './components/AdminPanel';
import Scanner from './components/Scanner';
import Home from './components/Home';

function App() {
  const location = useLocation();

  // Hide Navbar on Home Page
  const hideNavbar = location.pathname === '/';

  // Dynamic checks
  const role = localStorage.getItem('role');
  const userId = localStorage.getItem('userId');

  return (
    <>
      {!hideNavbar && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />

        {/* Scanner Route */}
        <Route path="/scanner/:sessionToken" element={<Scanner />} />

        {/* Admin Route */}
        <Route
          path="/admin"
          element={role === 'teacher' ? <AdminDashboard /> : <Navigate to="/login" />}
        />

        {/* Student Route (only allow if logged-in student is accessing their own dashboard) */}
        <Route
          path="/student/:id"
          element={
            role === 'student' && userId ? (
              <StudentDashboard />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </>
  );
}

export default App;
