import React, { useEffect, useRef } from 'react';
import { Html5Qrcode } from 'html5-qrcode';

function StudentPanel() {
  const scannerRef = useRef(null);

  useEffect(() => {
    const html5QrCode = new Html5Qrcode("qr-reader");

    Html5Qrcode.getCameras().then(devices => {
      if (devices && devices.length) {
        const cameraId = devices[0].id;
        html5QrCode.start(
          cameraId,
          {
            fps: 10,
            qrbox: { width: 250, height: 250 },
          },
          qrCodeMessage => {
            console.log(`QR Code detected: ${qrCodeMessage}`);
            // Handle scanned data here
            html5QrCode.stop(); // Optional: stop scanning after successful scan
          },
          errorMessage => {
            // Optional: console.log(`QR Code no match: ${errorMessage}`);
          }
        );
      }
    }).catch(err => {
      console.error("Camera error:", err);
    });

    return () => {
      html5QrCode.stop().catch(err => console.error('Stop error:', err));
    };
  }, []);

  return (
    <div>
      <h2>Student QR Scanner</h2>
      <div id="qr-reader" ref={scannerRef} style={{ width: '300px', height: '300px' }}></div>
    </div>
  );
}

export default StudentPanel;
