import React, { useState, useEffect } from 'react';
import API from '../api';
import QRReader from 'react-qr-reader';

export default function StudentPanel() {
  const [sessions, setSessions] = useState([]);
  const [scanning, setScanning] = useState(null);

  useEffect(() => {
    API.get('/sessions').then(r => setSessions(r.data));
  }, []);

  const handleScan = data => {
    if (data && scanning) {
      const { sessionCode } = JSON.parse(data);
      const userEmail = prompt('Enter your email'); // simple auth
      API.post('/attendance/mark', { sessionCode, userEmail })
         .then(() => { alert('Marked!'); setScanning(null); })
         .catch(e => alert(e.response?.data?.message || e.message));
    }
  };

  return (
    <div>
      <h2>Student Dashboard</h2>
      {scanning ? (
        <QRReader onScan={handleScan} onError={e => console.error(e)} style={{ width: '300px' }} />
      ) : (
        <ul>
          {sessions.map(s => (
            <li key={s._id}>
              {s.title} <button onClick={() => setScanning(s.sessionCode)}>Scan to Mark</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
