import React, { useEffect, useState } from 'react';
import API from '../api';
import { getUser } from '../utils';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const user = getUser();
  const [sessions, setSessions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (user?.role === 'student') {
      API.get('/sessions/active').then(res => setSessions(res.data));
    }
  }, [user]);

  return (
    <div>
      <h2>Welcome, {user?.name || 'Guest'}</h2>
      {user?.role === 'student' && (
        <>
          <h3>Available Sessions</h3>
          {sessions.map(s => (
            <div key={s._id}>
              {s.name} (expires {new Date(s.expiresAt).toLocaleString()})
              <button onClick={() => navigate(`/scanner/${s.token}`)}>
                Scan for this session
              </button>
            </div>
          ))}
        </>
      )}
      {user?.role === 'admin' && <p>Admin - use backend panel to manage sessions.</p>}
      {!user && <p>Please <a href="/login">Login</a> or <a href="/register">Register</a>.</p>}
    </div>
  );
}
