// src/App.js
import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Register from './components/Register';
import Login from './components/Login';
import StudentDashboard from './components/StudentPanel';
import AdminDashboard from './components/AdminPanel';
import Scanner from './components/Scanner';

// Check role from localStorage
const isTeacher = () => localStorage.getItem('role') === 'teacher';

function App() {
  return (
    <>
      <Navbar />
      <Routes>
        <Route path="/" element={<StudentDashboard />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/scanner/:sessionToken" element={<Scanner />} />
        <Route
          path="/admin"
          element={isTeacher() ? <AdminDashboard /> : <Navigate to="/login" />}
        />
      </Routes>
    </>
  );
}

export default App;
