// Dashboard Component
import React, { useEffect, useState } from 'react';
import axios from '../api';
import { getUser } from '../utils';
import Scanner from './Scanner';

export default function Dashboard() {
  const user = getUser();
  const [records, setRecords] = useState([]);

  useEffect(() => {
    if (user?.role === 'admin') {
      axios.get('/attendance/all').then(res => setRecords(res.data));
    }
  }, []);

  return (
    <div>
      <h2>Welcome, {user?.role}</h2>
      {user?.role === 'student' && <Scanner />}
      {user?.role === 'admin' && (
        <>
          <h3>Attendance Records</h3>
          <ul>
            {records.map((r, i) => (
              <li key={i}>
                {r.userId?.name} | {new Date(r.scannedAt).toLocaleString()}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
